function setHover(){for(var e=document.querySelectorAll(".product__item--selected"),t=0;t<e.length;t++)e[t].addEventListener("mouseleave",function(e){e.target.classList.add("hover-enabled")})}function selectProduct(){for(var e=document.querySelectorAll(".btn-buy"),t=document.querySelectorAll(".product__item--active"),o=0;o<t.length;o++)t[o].addEventListener("click",function(){var e=this;e.classList.remove("hover-enabled"),e.classList.toggle("product__item--selected"),setHover()});for(o=0;o<e.length;o++)e[o].addEventListener("click",function(){this.parentElement.previousElementSibling.classList.add("product__item--selected"),setHover()})}function showCatalog(){document.querySelector(".catalog").classList.add("catalog--visible")}document.addEventListener("DOMContentLoaded",function(e){setTimeout(showCatalog,200),selectProduct()});